	*
	*  This function will include 3rd party add-ons
	*
	*  @type	function
	*  @date	29/01/2014
	*  @since	5.0.0
	*
	*  @param	N/A
	*  @return	N/A
	*/
	
	function include_3rd_party() {
		
		// run only once
		if( $this->settings['include_3rd_party'] )
		{
			return false;
		}
		
		
		// update setting
		$this->settings['include_3rd_party'] = true;
		
		
